import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getLinkedInJobDetails } from '../../services/api';
import './JobListingPage.css';

// JobCard component fetches and displays job details for a given job ID.
const JobCard = ({ jobId }) => {
  const [job, setJob] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchJobDetails = async () => {
      try {
        const response = await getLinkedInJobDetails(jobId);
        if (response && response.success) {
          setJob(response.data);
        } else {
          setError('No job data available');
        }
      } catch (err) {
        console.error('Error fetching job details:', err);
        setError('Unable to load job details');
      } finally {
        setLoading(false);
      }
    };

    fetchJobDetails();
  }, [jobId]);

  if (loading) return <p>Loading job details...</p>;
  if (error) return <p>{error}</p>;
  if (!job) return null;

  return (
    <div className="job-card">
      <div className="job-header">
        {job.company?.logo && (
          <img src={job.company.logo} alt={job.company.name} className="company-logo" />
        )}
        <h3>{job.title}</h3>
      </div>
      <p className="company">
        {job.company?.name} | {job.location}
      </p>
      <p className="description">
        {job.description
          ? job.description.slice(0, 150) + '...'
          : 'No description available.'}
      </p>
      <Link to={`/jobs/${jobId}`} className="btn btn-primary">
        Apply Now
      </Link>
    </div>
  );
};

const JobListingPage = () => {
  // For demonstration, we use a static list of job IDs.
  const [jobIds] = useState(['4090994054', '4090994054', '4090994054']);
  const [filter, setFilter] = useState('');

  // For filtering, we simply filter the jobIds (if needed, update your filtering logic)
  const filteredJobIds = jobIds.filter((id) => id.toLowerCase().includes(filter.toLowerCase()));

  return (
    <div className="job-listing-container container">
      <aside className="filters">
        <h3>Filters</h3>
        <input
          type="text"
          placeholder="Search jobs..."
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        />
        <button>Search</button>
      </aside>
      <section className="jobs-section">
        <div className="jobs-header">
          <p>Showing {filteredJobIds.length} results</p>
          <select>
            <option>Most Recent</option>
            <option>Highest Paying</option>
            <option>Location</option>
          </select>
        </div>
        <div className="jobs-grid">
          {filteredJobIds.length > 0 ? (
            filteredJobIds.map((id, index) => <JobCard key={index} jobId={id} />)
          ) : (
            <p>No jobs found.</p>
          )}
        </div>
      </section>
    </div>
  );
};

export default JobListingPage;
