// src/pages/Profile/ProfilePage.js
import React, { useState, useEffect } from 'react';
import { updateUserProfile, getUserProfile } from '../../services/api';
import { useNavigate } from 'react-router-dom';
import './ProfilePage.css';

const ProfilePage = () => {
  const navigate = useNavigate();
  const [profile, setProfile] = useState({
    fullName: '',
    email: '',
    education: '',
    skills: '',
    experience: '',
    careerGoals: '',
    resume: null,
  });
  const [errors, setErrors] = useState({});
  const [message, setMessage] = useState('');

  // Fetch profile data when component mounts
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const data = await getUserProfile();
        if (data) {
          setProfile({
            fullName: data.fullName || '',
            email: data.email || '',
            education: data.education || '',
            skills: data.skills || '',
            experience: data.experience || '',
            careerGoals: data.careerGoals || '',
            resume: null,
          });
        }
      } catch (error) {
        console.error('Error fetching profile:', error);
      }
    };

    fetchProfile();
  }, []);

  // Simple email regex validation
  const isValidEmail = (email) => {
    const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return regex.test(email);
  };

  // Validate required fields
  const validateProfile = () => {
    let validationErrors = {};

    if (!profile.fullName.trim()) {
      validationErrors.fullName = 'Full name is required';
    }
    if (!profile.email.trim()) {
      validationErrors.email = 'Email is required';
    } else if (!isValidEmail(profile.email)) {
      validationErrors.email = 'Invalid email format';
    }
    // Additional validations for other fields can be added here

    return validationErrors;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfile((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setProfile((prev) => ({ ...prev, resume: file }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validateProfile();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return; // Stop submission if there are errors
    }
    setErrors({}); // Clear errors if validations pass

    try {
      const formData = new FormData();
      formData.append('fullName', profile.fullName);
      formData.append('email', profile.email);
      formData.append('education', profile.education);
      formData.append('skills', profile.skills);
      formData.append('experience', profile.experience);
      formData.append('careerGoals', profile.careerGoals);
      if (profile.resume) {
        formData.append('resume', profile.resume);
      }

      await updateUserProfile(formData);
      setMessage('Profile updated successfully!');
      // Optionally redirect after successful update:
      // navigate('/');
    } catch (error) {
      console.error('Error updating profile:', error);
      setMessage('Error updating profile');
    }
  };

  return (
    <div className="profile-container container">
      <h2>Build Your Job Profile</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data" className="profile-form">
        <div className="form-group">
          <label>Full Name</label>
          <input
            type="text"
            name="fullName"
            value={profile.fullName}
            onChange={handleChange}
            placeholder="Your full name"
            required
          />
          {errors.fullName && <p className="error">{errors.fullName}</p>}
        </div>
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={profile.email}
            onChange={handleChange}
            placeholder="Your email"
            required
          />
          {errors.email && <p className="error">{errors.email}</p>}
        </div>
        <div className="form-group">
          <label>Education</label>
          <input
            type="text"
            name="education"
            value={profile.education}
            onChange={handleChange}
            placeholder="Your highest qualification"
          />
        </div>
        <div className="form-group">
          <label>Skills</label>
          <input
            type="text"
            name="skills"
            value={profile.skills}
            onChange={handleChange}
            placeholder="List your skills, separated by commas"
          />
        </div>
        <div className="form-group">
          <label>Work Experience</label>
          <textarea
            name="experience"
            value={profile.experience}
            onChange={handleChange}
            placeholder="Describe your work experience"
          ></textarea>
        </div>
        <div className="form-group">
          <label>Career Goals</label>
          <textarea
            name="careerGoals"
            value={profile.careerGoals}
            onChange={handleChange}
            placeholder="Describe your career goals"
          ></textarea>
        </div>
        <div className="form-group">
          <label>Upload Resume</label>
          <input type="file" name="resume" onChange={handleFileChange} />
        </div>
        <button type="submit" className="btn btn-primary">Save Profile</button>
      </form>
      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default ProfilePage;
