import axios from 'axios';

// Define base URLs for your local authentication and profile endpoints
const AUTH_URL = 'http://localhost:5000/api/auth';
const API_BASE_URL = 'http://localhost:5000/api';

// Jobicy API configuration for remote jobs
const JOBICY_URL = 'https://jobicy.p.rapidapi.com/api/v2/remote-jobs';
const RAPIDAPI_KEY = 'b9fa4a9482msh0051404b674a43fp1398fajsn5decfeb2d616';
const JOBICY_API_HOST = 'jobicy.p.rapidapi.com';

// LinkedIn Data API configuration
const LINKEDIN_API_BASE_URL = 'https://linkedin-data-api.p.rapidapi.com';
const LINKEDIN_API_HOST = 'linkedin-data-api.p.rapidapi.com';

// --- Authentication & Profile Functions ---
export const registerUser = async (userData) => {
  try {
    const response = await axios.post(`${AUTH_URL}/register`, userData);
    return response.data;
  } catch (error) {
    throw error.response.data;
  }
};

export const loginUser = async (userData) => {
  try {
    const response = await axios.post(`${AUTH_URL}/login`, userData);
    return response.data;
  } catch (error) {
    throw error.response.data;
  }
};

export const getUserProfile = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/profile`, {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
    });
    return response.data;
  } catch (error) {
    console.error('Error fetching profile:', error);
    throw error;
  }
};

export const updateUserProfile = async (formData) => {
  try {
    const response = await axios.put(`${API_BASE_URL}/profile`, formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
    });
    return response.data;
  } catch (error) {
    console.error('Error updating profile:', error);
    throw error;
  }
};

// --- Remote Jobs Function (Jobicy) ---
// This function fetches remote jobs using the Jobicy API endpoint.
export const getRemoteJobs = async () => {
  try {
    const response = await axios.get(JOBICY_URL, {
      headers: {
        'x-rapidapi-key': RAPIDAPI_KEY,
        'x-rapidapi-host': JOBICY_API_HOST,
      },
    });
    // Adjust based on Jobicy's response structure.
    // For example, if the jobs are returned under a "data" key:
    return response.data.data; 
  } catch (error) {
    console.error('Error fetching remote jobs from Jobicy:', error);
    throw error;
  }
};

// --- LinkedIn Data API Function ---
// This function fetches job details from the LinkedIn Data API.
export const getLinkedInJobDetails = async (jobId) => {
  try {
    const response = await axios.get(`${LINKEDIN_API_BASE_URL}/get-job-details`, {
      params: { id: jobId },
      headers: {
        'x-rapidapi-key': RAPIDAPI_KEY,
        'x-rapidapi-host': LINKEDIN_API_HOST,
      },
    });
    return response.data;
  } catch (error) {
    console.error('Error fetching job details from LinkedIn:', error);
    throw error;
  }
};

// --- Proxy Function to Call Backend ---
// This function calls your own backend endpoint to fetch job details,
// keeping your API key hidden on the server.
export const getJobDetailsFromBackend = async (jobId) => {
  const response = await axios.get(`http://localhost:5000/api/rapid/jobdetails`, {
    params: { jobId }
  });
  return response.data;
};
