// src/pages/Profile/ProfilePage.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ProfilePage.css';

const ProfilePage = () => {
  // Retrieve userId from localStorage; for testing, use 'defaultUserId'
  const userId = localStorage.getItem('userId') || 'defaultUserId';

  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  
  // Form state for editing profile details
  const [formData, setFormData] = useState({
    name: '',
    avatar: '',
    headline: '',
    summary: '',
    connectionsCount: 0,
    experience: '', // As JSON string
    education: ''   // As JSON string
  });
  
  // State to determine if the profile is locked (read-only)
  const [isLocked, setIsLocked] = useState(false);
  // Message to show after profile is submitted/locked
  const [lockMessage, setLockMessage] = useState('');

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/profile?userId=${userId}`);
        if (response.data.success) {
          const data = response.data.data;
          if (Object.keys(data).length === 0) {
            // No profile exists; form remains empty and editable
            setProfile(null);
            setIsLocked(false);
          } else {
            setProfile(data);
            setFormData({
              name: data.name || '',
              avatar: data.avatar || '',
              headline: data.headline || '',
              summary: data.summary || '',
              connectionsCount: data.connectionsCount || 0,
              experience: data.experience ? JSON.stringify(data.experience) : '',
              education: data.education ? JSON.stringify(data.education) : ''
            });
            // Once a profile exists, lock the fields
            setIsLocked(true);
            setLockMessage('Your profile has been submitted and locked. Click "Edit" to make changes.');
          }
        } else {
          setError('Failed to fetch profile.');
        }
      } catch (err) {
        console.error('Error fetching profile:', err);
        setError('Error fetching profile.');
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [userId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Convert JSON string fields into arrays if provided
      const expParsed = formData.experience ? JSON.parse(formData.experience) : [];
      const eduParsed = formData.education ? JSON.parse(formData.education) : [];
      if (!profile) {
        // Create a new profile
        const response = await axios.post('http://localhost:5000/api/profile', {
          userId,
          ...formData,
          experience: expParsed,
          education: eduParsed
        });
        if (response.data.success) {
          alert('Profile created successfully!');
          setProfile(response.data.data || formData);
          setIsLocked(true);
          setLockMessage('Your profile has been submitted and locked. Click "Edit" to make changes.');
        }
      } else {
        // Update existing profile
        const response = await axios.put('http://localhost:5000/api/profile', {
          userId,
          ...formData,
          experience: expParsed,
          education: eduParsed
        });
        if (response.data.success) {
          alert('Profile updated successfully!');
          setIsLocked(true);
          setLockMessage('Your profile has been updated and locked. Click "Edit" to make further changes.');
        }
      }
    } catch (err) {
      console.error('Error saving profile:', err);
      setError('Error saving profile.');
    }
  };

  const handleEdit = () => {
    // Unlock the form so the user can edit
    setIsLocked(false);
    setLockMessage('');
  };

  if (loading) return <p>Loading profile...</p>;
  if (error) return <p className="error">{error}</p>;

  return (
    <div className="profile-page container">
      <h1>My Profile</h1>
      {lockMessage && <p className="lock-message">{lockMessage}</p>}
      <form onSubmit={handleSubmit} className="profile-form">
        <div className="form-group">
          <label>Name:</label>
          <input 
            type="text" 
            name="name" 
            value={formData.name} 
            onChange={handleChange} 
            placeholder="Enter your name" 
            disabled={isLocked}
          />
        </div>
        <div className="form-group">
          <label>Avatar URL:</label>
          <input 
            type="text" 
            name="avatar" 
            value={formData.avatar} 
            onChange={handleChange} 
            placeholder="Enter avatar URL" 
            disabled={isLocked}
          />
        </div>
        <div className="form-group">
          <label>Headline:</label>
          <input 
            type="text" 
            name="headline" 
            value={formData.headline} 
            onChange={handleChange} 
            placeholder="Enter your headline" 
            disabled={isLocked}
          />
        </div>
        <div className="form-group">
          <label>Summary:</label>
          <textarea 
            name="summary" 
            value={formData.summary} 
            onChange={handleChange} 
            placeholder="Enter your summary" 
            disabled={isLocked}
          ></textarea>
        </div>
        <div className="form-group">
          <label>Connections Count:</label>
          <input 
            type="number" 
            name="connectionsCount" 
            value={formData.connectionsCount} 
            onChange={handleChange} 
            disabled={isLocked}
          />
        </div>
        <div className="form-group">
          <label>Experience (JSON):</label>
          <textarea 
            name="experience" 
            value={formData.experience} 
            onChange={handleChange} 
            placeholder='e.g., [{"company": "TechCorp", "role": "Engineer", "duration": "2 years"}]' 
            disabled={isLocked}
          ></textarea>
        </div>
        <div className="form-group">
          <label>Education (JSON):</label>
          <textarea 
            name="education" 
            value={formData.education} 
            onChange={handleChange} 
            placeholder='e.g., [{"school": "University", "degree": "B.Sc", "year": "2019"}]' 
            disabled={isLocked}
          ></textarea>
        </div>
        {isLocked ? (
          <button type="button" className="btn btn-secondary" onClick={handleEdit}>
            Edit Profile
          </button>
        ) : (
          <button type="submit" className="btn btn-primary">
            Save Profile
          </button>
        )}
      </form>
    </div>
  );
};

export default ProfilePage;
