// src/pages/Profile/ProfilePage.js
import React, { useState, useEffect } from 'react';
import { updateUserProfile, getUserProfile } from '../../services/api';
import { useNavigate } from 'react-router-dom';
import './ProfilePage.css';

const ProfilePage = () => {
  const navigate = useNavigate();
  const [profile, setProfile] = useState({
    fullName: '',
    email: '',
    education: '',
    skills: '',
    experience: '',
    careerGoals: '',
    resume: null,
  });
  const [errors, setErrors] = useState({});
  const [message, setMessage] = useState('');

  // Fetch profile data when component mounts
  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const data = await getUserProfile();
        if (data) {
          setProfile({
            fullName: data.fullName || '',
            email: data.email || '',
            education: data.education || '',
            skills: data.skills || '',
            experience: data.experience || '',
            careerGoals: data.careerGoals || '',
            resume: null, // file won't be auto-populated
          });
        }
      } catch (error) {
        console.error('Error fetching profile:', error);
      }
    };

    fetchProfile();
  }, []);

  // Validate a field on blur
  const validateField = (name, value) => {
    if (!value || !value.trim()) {
      switch (name) {
        case 'fullName':
          return 'Full Name is required';
        case 'email':
          return 'Email is required';
        case 'education':
          return 'Education is required';
        case 'skills':
          return 'Skills are required';
        case 'experience':
          return 'Work Experience is required';
        case 'careerGoals':
          return 'Career Goals are required';
        default:
          return 'This field is required';
      }
    }
    if (name === 'email') {
      const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
      if (!regex.test(value)) {
        return 'Invalid email format';
      }
    }
    return '';
  };

  const handleBlur = (e) => {
    const { name, value } = e.target;
    const error = validateField(name, value);
    setErrors((prev) => ({ ...prev, [name]: error }));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProfile((prev) => ({ ...prev, [name]: value }));
    // Optionally clear error on change
    setErrors((prev) => ({ ...prev, [name]: '' }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setProfile((prev) => ({ ...prev, resume: file }));
    // Validate file input immediately if mandatory
    if (!file) {
      setErrors((prev) => ({ ...prev, resume: 'Resume is required' }));
    } else {
      setErrors((prev) => ({ ...prev, resume: '' }));
    }
  };

  // Validate all fields before submission
  const validateProfile = () => {
    const validationErrors = {};
    Object.entries(profile).forEach(([key, value]) => {
      // For resume, check that a file is selected
      if (key === 'resume') {
        if (!value) {
          validationErrors.resume = 'Resume is required';
        }
      } else {
        const error = validateField(key, value);
        if (error) {
          validationErrors[key] = error;
        }
      }
    });
    return validationErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validateProfile();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
      return; // Prevent form submission if there are errors
    }
    setErrors({}); // Clear errors if validations pass

    try {
      const formData = new FormData();
      formData.append('fullName', profile.fullName);
      formData.append('email', profile.email);
      formData.append('education', profile.education);
      formData.append('skills', profile.skills);
      formData.append('experience', profile.experience);
      formData.append('careerGoals', profile.careerGoals);
      if (profile.resume) {
        formData.append('resume', profile.resume);
      }

      await updateUserProfile(formData);
      setMessage('Profile updated successfully!');
      // Optionally redirect after successful update:
      // navigate('/');
    } catch (error) {
      console.error('Error updating profile:', error);
      setMessage('Error updating profile');
    }
  };

  return (
    <div className="profile-container container">
      <h2>Build Your Job Profile</h2>
      <form onSubmit={handleSubmit} encType="multipart/form-data" className="profile-form">
        <div className="form-group">
          <label>Full Name</label>
          <input
            type="text"
            name="fullName"
            value={profile.fullName}
            onChange={handleChange}
            onBlur={handleBlur}
            placeholder="Your full name"
            required
          />
          {errors.fullName && <p className="error">{errors.fullName}</p>}
        </div>
        <div className="form-group">
          <label>Email</label>
          <input
            type="email"
            name="email"
            value={profile.email}
            onChange={handleChange}
            onBlur={handleBlur}
            placeholder="Your email"
            required
          />
          {errors.email && <p className="error">{errors.email}</p>}
        </div>
        <div className="form-group">
          <label>Education</label>
          <input
            type="text"
            name="education"
            value={profile.education}
            onChange={handleChange}
            onBlur={handleBlur}
            placeholder="Your highest qualification"
            required
          />
          {errors.education && <p className="error">{errors.education}</p>}
        </div>
        <div className="form-group">
          <label>Skills</label>
          <input
            type="text"
            name="skills"
            value={profile.skills}
            onChange={handleChange}
            onBlur={handleBlur}
            placeholder="List your skills, separated by commas"
            required
          />
          {errors.skills && <p className="error">{errors.skills}</p>}
        </div>
        <div className="form-group">
          <label>Work Experience</label>
          <textarea
            name="experience"
            value={profile.experience}
            onChange={handleChange}
            onBlur={handleBlur}
            placeholder="Describe your work experience"
            required
          ></textarea>
          {errors.experience && <p className="error">{errors.experience}</p>}
        </div>
        <div className="form-group">
          <label>Career Goals</label>
          <textarea
            name="careerGoals"
            value={profile.careerGoals}
            onChange={handleChange}
            onBlur={handleBlur}
            placeholder="Describe your career goals"
            required
          ></textarea>
          {errors.careerGoals && <p className="error">{errors.careerGoals}</p>}
        </div>
        <div className="form-group">
          <label>Upload Resume</label>
          <input
            type="file"
            name="resume"
            onChange={handleFileChange}
            required
          />
          {errors.resume && <p className="error">{errors.resume}</p>}
        </div>
        <button type="submit" className="btn btn-primary">Save Profile</button>
      </form>
      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default ProfilePage;
