// src/pages/Jobs/JobListingPage.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { getRemoteJobs } from '../../services/api'; // or your own API endpoint
import './JobListingPage.css';

const JobListingPage = () => {
  const [jobs, setJobs] = useState([]);
  const [filter, setFilter] = useState('');

  useEffect(() => {
    // Replace with your real data API call
    const fetchJobs = async () => {
      try {
        const data = await getRemoteJobs();
        setJobs(data);
      } catch (error) {
        console.error('Error fetching jobs:', error);
      }
    };

    fetchJobs();
  }, []);

  const filteredJobs = jobs.filter(
    (job) =>
      job.title?.toLowerCase().includes(filter.toLowerCase()) ||
      job.company_name?.toLowerCase().includes(filter.toLowerCase())
  );

  return (
    <div className="job-listing-container container">
      <aside className="filters">
        <h3>Filters</h3>
        <input
          type="text"
          placeholder="Search jobs..."
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        />
      </aside>
      <section className="jobs-section">
        <div className="jobs-header">
          <p>Showing {filteredJobs.length} results</p>
          <select>
            <option>Most Recent</option>
            <option>Highest Paying</option>
            <option>Location</option>
          </select>
        </div>
        <div className="jobs-grid">
          {filteredJobs.length > 0 ? (
            filteredJobs.map((job) => (
              <div key={job.id} className="job-card">
                <h3>{job.title}</h3>
                <p>{job.company_name} | {job.candidate_required_location}</p>
                <Link to={`/jobs/${job.id}`} className="btn btn-primary">
                  Apply Now
                </Link>
              </div>
            ))
          ) : (
            <p>No jobs found.</p>
          )}
        </div>
      </section>
    </div>
  );
};

export default JobListingPage;
