// frontend/src/pages/Community/CommunityPage.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './CommunityPage.css';

const CommunityPage = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/community');
        if (response.data.success) {
          setPosts(response.data.data);
        }
      } catch (err) {
        console.error('Error fetching community posts:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchPosts();
  }, []);

  if (loading) return <p>Loading community posts...</p>;

  return (
    <div className="community-page container">
      <h1>Community</h1>
      <div className="community-grid">
        {posts.map((post) => (
          <div key={post.id} className="community-card">
            <div className="community-header">
              <img src={post.author.avatar} alt={post.author.name} className="avatar" />
              <div className="author-info">
                <h3>{post.author.name}</h3>
                <p>{post.author.headline}</p>
              </div>
            </div>
            <p className="post-content">{post.content}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CommunityPage;
