import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
// Import searchJobs along with getEstimatedSalary
import { searchJobs, getEstimatedSalary } from '../../services/api';
import './JobListingPage.css';

// Inline SalaryDisplay component remains the same
const SalaryDisplay = ({ jobTitle, location }) => {
  const [salary, setSalary] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSalary = async () => {
      try {
        const data = await getEstimatedSalary(jobTitle, location, 'ANY', 'ALL');
        // Adjust based on your API response structure.
        setSalary(data.estimated_salary);
      } catch (err) {
        console.error("Error fetching salary:", err);
        setError("Salary info unavailable");
      } finally {
        setLoading(false);
      }
    };

    fetchSalary();
  }, [jobTitle, location]);

  if (loading) return <p>Loading salary...</p>;
  if (error) return <p>{error}</p>;
  return <p>Estimated Salary: {salary}</p>;
};

const JobListingPage = () => {
  const [jobs, setJobs] = useState([]);
  const [filter, setFilter] = useState('');

  // Function to fetch jobs using the searchJobs API endpoint.
  // The default parameters here mirror the URL you provided.
  const fetchJobs = async (query) => {
    try {
      const data = await searchJobs(query, 1, 1, 'us', 'all');
      // Assuming the API returns an object with a "jobs" property:
      setJobs(data.jobs);
    } catch (error) {
      console.error('Error fetching jobs:', error);
    }
  };

  // On initial load, fetch a default list of jobs.
  useEffect(() => {
    fetchJobs('developer jobs in chicago');
  }, []);

  // When the user clicks the search button, fetch jobs with the entered query.
  const handleSearch = () => {
    fetchJobs(filter);
  };

  return (
    <div className="job-listing-container container">
      <aside className="filters">
        <h3>Filters</h3>
        <input
          type="text"
          placeholder="Search jobs..."
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </aside>
      <section className="jobs-section">
        <div className="jobs-header">
          <p>Showing {jobs.length} results</p>
          <select>
            <option>Most Recent</option>
            <option>Highest Paying</option>
            <option>Location</option>
          </select>
        </div>
        <div className="jobs-grid">
          {jobs.length > 0 ? (
            jobs.map((job) => (
              <div key={job.id} className="job-card">
                <h3>{job.title}</h3>
                <p>
                  {job.company_name} | {job.candidate_required_location}
                </p>
                {/* Display salary information inline */}
                <SalaryDisplay
                  jobTitle={job.title}
                  location={job.candidate_required_location}
                />
                <Link to={`/jobs/${job.id}`} className="btn btn-primary">
                  Apply Now
                </Link>
              </div>
            ))
          ) : (
            <p>No jobs found.</p>
          )}
        </div>
      </section>
    </div>
  );
};

export default JobListingPage;
