// src/pages/Jobs/JobListingPage.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { searchJobs, getEstimatedSalary } from '../../services/api';
import './JobListingPage.css';

// Inline SalaryDisplay component (updated)
const SalaryDisplay = ({ jobTitle, location }) => {
  const [salary, setSalary] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchSalary = async () => {
      try {
        const response = await getEstimatedSalary(jobTitle, location, 'ANY', 'ALL');
        // Assume the API response has a "data" array containing salary info
        if (response && response.data && response.data.length > 0) {
          setSalary(response.data[0]);
        } else {
          setError("No salary data available");
        }
      } catch (err) {
        console.error("Error fetching salary:", err);
        setError("Salary info unavailable");
      } finally {
        setLoading(false);
      }
    };

    fetchSalary();
  }, [jobTitle, location]);

  if (loading) return <p className="salary-loading">Loading salary...</p>;
  if (error) return <p className="salary-error">{error}</p>;

  return (
    <div className="salary-display">
      <p>
        Salary Range: ${salary.min_salary.toFixed(2)} - ${salary.max_salary.toFixed(2)} {salary.salary_currency} / {salary.salary_period.toLowerCase()}
      </p>
      <p>Median: ${salary.median_salary.toFixed(2)}</p>
      <p>Confidence: {salary.confidence}</p>
    </div>
  );
};

// Dedicated JobCard component to display each job's details
const JobCard = ({ job }) => {
  return (
    <div className="job-card">
      <div className="job-header">
        {job.employer_logo && (
          <img
            src={job.employer_logo}
            alt={job.employer_name}
            className="employer-logo"
          />
        )}
        <h3>{job.job_title}</h3>
      </div>
      <p className="company">
        {job.employer_name} | {job.job_location || job.location}
      </p>
      <p className="description">
        {job.job_description
          ? job.job_description.slice(0, 150) + "..."
          : "No description available."}
      </p>
      {/* Inline salary information */}
      <SalaryDisplay
        jobTitle={job.job_title}
        location={job.job_location || job.location}
      />
      <Link to={`/jobs/${job.job_id}`} className="btn btn-primary">
        Apply Now
      </Link>
    </div>
  );
};

const JobListingPage = () => {
  const [jobs, setJobs] = useState([]);
  const [filter, setFilter] = useState('');

  // Fetch jobs using the searchJobs API endpoint.
  // The API returns an object with a "data" key containing an array of jobs.
  const fetchJobs = async (query) => {
    try {
      const response = await searchJobs(query, 1, 1, 'us', 'all');
      setJobs(response.data);
    } catch (error) {
      console.error('Error fetching jobs:', error);
    }
  };

  // On initial load, fetch a default query.
  useEffect(() => {
    fetchJobs('developer jobs in chicago');
  }, []);

  // Filter jobs based on job title or employer name.
  const filteredJobs = jobs.filter(
    (job) =>
      job.job_title?.toLowerCase().includes(filter.toLowerCase()) ||
      job.employer_name?.toLowerCase().includes(filter.toLowerCase())
  );

  // Handle the search button click.
  const handleSearch = () => {
    fetchJobs(filter);
  };

  return (
    <div className="job-listing-container container">
      <aside className="filters">
        <h3>Filters</h3>
        <input
          type="text"
          placeholder="Search jobs..."
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </aside>
      <section className="jobs-section">
        <div className="jobs-header">
          <p>Showing {filteredJobs.length} results</p>
          <select>
            <option>Most Recent</option>
            <option>Highest Paying</option>
            <option>Location</option>
          </select>
        </div>
        <div className="jobs-grid">
          {filteredJobs.length > 0 ? (
            filteredJobs.map((job) => <JobCard key={job.job_id} job={job} />)
          ) : (
            <p>No jobs found.</p>
          )}
        </div>
      </section>
    </div>
  );
};

export default JobListingPage;
