// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Layout from './components/Layout/Layout';
import HomePage from './pages/Home/HomePage';
import LoginPage from './pages/Auth/LoginPage';
import RegisterPage from './pages/Auth/RegisterPage';
import JobListingPage from './pages/Jobs/JobListingPage';
import ProfilePage from './pages/Profile/ProfilePage';
import CommunityPage from './pages/Community/CommunityPage';

const App = () => {
  const token = localStorage.getItem('token');

  return (
    <Router>
      <Routes>
        <Route element={<Layout />}>
          <Route path="/" element={token ? <HomePage /> : <Navigate to="/login" replace />} />
          <Route path="/jobs" element={token ? <JobListingPage /> : <Navigate to="/login" replace />} />
          <Route path="/profile" element={token ? <ProfilePage /> : <Navigate to="/login" replace />} />
          <Route path="/community" element={token ? <CommunityPage /> : <Navigate to="/login" replace />} />
        </Route>
        <Route path="/login" element={<LoginPage />} />
        <Route path="/register" element={<RegisterPage />} />
      </Routes>
    </Router>
  );
};

export default App;
