import axios from 'axios';

// Base URLs
const AUTH_URL = 'http://localhost:5000/api/auth';
const API_BASE_URL = 'http://localhost:5000/api';
const REMOTIVE_URL = 'https://remotive.io/api/remote-jobs';

/* Authentication APIs */

// Register a new user
export const registerUser = async (userData) => {
  try {
    const response = await axios.post(`${AUTH_URL}/register`, userData);
    return response.data;
  } catch (error) {
    // Pass the error response from the backend
    throw error.response.data;
  }
};

// Login user and retrieve token
export const loginUser = async (userData) => {
  try {
    const response = await axios.post(`${AUTH_URL}/login`, userData);
    return response.data;
  } catch (error) {
    throw error.response.data;
  }
};

/* Jobs API */

// Fetch remote jobs from Remotive API
export const getRemoteJobs = async () => {
  try {
    const response = await axios.get(REMOTIVE_URL);
    // Remotive returns job listings in response.data.jobs
    return response.data.jobs;
  } catch (error) {
    console.error('Error fetching remote jobs:', error);
    throw error;
  }
};

/* Profile APIs */

// Fetch user profile (requires authentication token)
export const getUserProfile = async () => {
  try {
    const response = await axios.get(`${API_BASE_URL}/profile`, {
      headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
    });
    return response.data;
  } catch (error) {
    console.error('Error fetching profile:', error);
    throw error;
  }
};

// Update user profile (with file upload support)
export const updateUserProfile = async (formData) => {
  try {
    const response = await axios.put(`${API_BASE_URL}/profile`, formData, {
      headers: {
        'Content-Type': 'multipart/form-data',
        Authorization: `Bearer ${localStorage.getItem('token')}`,
      },
    });
    return response.data;
  } catch (error) {
    console.error('Error updating profile:', error);
    throw error;
  }
};
